<script>

  // Increment Animation
  var speed = 200;
  var i = 163417975000000;
  //var i = 7200000000;

  const element = document.getElementById("nbr");
  const maxNumber = 163417976000000;
  //const maxNumber = 9000000000;

  Number.prototype.format = function (n) {
    var r = new RegExp("\\d(?=(\\d{3})+" + (n > 0 ? "\\." : "$") + ")", "g");
    return this.toFixed(Math.max(0, Math.floor(n))).replace(r, "$&,");
  };

  const increment = () => {
    const interval = setInterval(() => {
      i += Number(8327);
      if (i > maxNumber) {
        element.innerHTML = maxNumber.format();
        clearInterval(interval);

        setTimeout(() => {
          //i = 7200000000
          i = 163417975000000;
          increment();
        }, 2000);
      } else {
        element.innerHTML = i.format();
      }
    }, speed);
  };

  jQuery(document).ready(function ($) {
    increment();
  });
  // End of Increment Animation

  // Video story configuration
  // Get references to the video element and the play button
  var video = document.getElementById("story-video-en");
  var playButton = document.getElementById("story-play-btn");
  var pauseButton = document.getElementById("story-pause-btn");

  // Add event listener to the play button
  playButton.addEventListener("click", function() {
    // Check if the video is paused
    if (video.paused) {
      // If paused, play the video
      video.play();
    }
  });

  // Add event listener to the original pause button
  pauseButton.addEventListener("click", function() {
    // Pause the video
    video.pause();
  });

  // Get reference to the new pause trigger by class
  var mobileCloseButton = document.getElementById('story-pause-btn-mobile');

  // Add event listener to the new pause trigger
  mobileCloseButton.addEventListener('click', function() {
    // Pause the video
    video.pause();
  });

</script></body></html>